                            <th>{{bien.prix}}</th>
                        
                           
                               <th>
                             {% set c=0 %}
                                 {% for i in bien.image %}
                                 {% if c==0 %}
                                   <img src=\"{{asset('images/properties/'~i.image)}}\" class=\"properties\" alt=\"properties\" />
                                       {% set c=c+1 %}
                                   {% endif %}
                                   {% endfor %}
</th>
                              <th>{{bien.description}}</th>
                                <th>{{\"action\"}}</th>
                 
     
                         </tr>
                    {% endfor %}
                    
                                     
                                    </tbody>
                                </table>

</form>
